content.query:
-- see external data processed
SELECT * FROM sys.dm_external_data_processed;

-- see current settings dail.weekly,monthly 
SELECT * FROM sys.configurations
WHERE name LIKE 'Data Processed%';

-- set data process limit
sp_set_data_processed_limit
    @type = N'monthly',
    @limit_tb = 2 ;

sp_set_data_processed_limit
    @type = N'weekly',
    @limit_tb = 1 ;

sp_set_data_processed_limit
    @type = N'daily',
    @limit_tb = 1 ;
